package principal;

public class Program {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
				
		//Declaração de constatnte
		final double PI = 3.14151617;
		
		//função para calculo de potencia
		System.out.println(Math.pow(3, 3));
		
		// retorn o maior valor
		System.out.println(Math.max(5, 10));
		
		// retorna o menor valor
		System.out.println(Math.min(1, 3));
		
		//Raiz quadrada
		System.out.println(Math.sqrt(16));
		
		// arredondada o valor 
		System.out.println(Math.round(25.4999));
		
		//multiplica 2 pelo valor de PI
		System.out.println(2 * Math.PI);
		
		System.out.println(Math.PI);
	}

}
